import React, { useState, useEffect } from 'react';
import StudySchedule from './components/StudySchedule';
import SubjectSelector from './components/SubjectSelector';
import VideoResults from './components/VideoResults';
import LoadingSkeleton from './components/LoadingSkeleton';
import ThemeToggle from './components/ThemeToggle';
import { searchVideos, getPopularVideos } from './services/youtubeApi';
import './App.css';

function App() {
  const [videos, setVideos] = useState([]);
  const [loading, setLoading] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [error, setError] = useState('');
  const [hasSearched, setHasSearched] = useState(false);
  const [selectedSubject, setSelectedSubject] = useState('');
  const [studySchedule, setStudySchedule] = useState({});
  const [currentView, setCurrentView] = useState('schedule'); // 'schedule' or 'videos'

  // Sayfa yüklendiğinde YKS eğitim videolarını getir
  useEffect(() => {
    loadYKSVideos();
  }, []);

  const loadYKSVideos = async () => {
    try {
      setLoading(true);
      setError('');
      const yksVideos = await searchVideos('YKS TYT AYT matematik fizik kimya biyoloji');
      setVideos(yksVideos);
      setHasSearched(false);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleSubjectSearch = async (subject, topic) => {
    if (!subject || !topic) return;
    
    setLoading(true);
    setSearchQuery(`${subject} ${topic}`);
    setError('');
    setHasSearched(true);
    setCurrentView('videos');
    
    try {
      const query = `${subject} ${topic} YKS TYT AYT ders anlatım`;
      const results = await searchVideos(query);
      setVideos(results);
    } catch (err) {
      setError(err.message);
      setVideos([]);
    } finally {
      setLoading(false);
    }
  };

  const handleScheduleUpdate = (day, timeSlot, subject, topic) => {
    const newSchedule = {
      ...studySchedule,
      [day]: {
        ...studySchedule[day],
        [timeSlot]: { subject, topic }
      }
    };
    setStudySchedule(newSchedule);
    localStorage.setItem('studySchedule', JSON.stringify(newSchedule));
  };

  const loadScheduleFromStorage = () => {
    const saved = localStorage.getItem('studySchedule');
    if (saved) {
      setStudySchedule(JSON.parse(saved));
    }
  };

  useEffect(() => {
    loadScheduleFromStorage();
  }, []);

  return (
    <div className="App">
      <ThemeToggle />
      <header className="app-header">
        <div className="container">
          <h1 className="app-title">
            <span className="youtube-red">YKS</span> Ders Programı
          </h1>
          <p className="app-subtitle">Haftalık çalışma programınızı hazırlayın ve her konu için en iyi videoları bulun</p>
        </div>
      </header>

      <main className="app-main">
        <div className="container">
          <SearchBar onSearch={handleSearch} loading={loading} />
          <CategoryFilter 
            selectedCategory={selectedCategory}
            onCategoryChange={handleCategoryChange}
            loading={loading}
          />
          
          {loading && (
            <div className="loading-container">
              <div className="loading-spinner"></div>
              <p>{hasSearched ? 'Aranıyor...' : 'Popüler videolar yükleniyor...'}</p>
              <LoadingSkeleton count={6} />
            </div>
          )}

          {error && (
            <div className="error-container">
              <h3>Hata Oluştu</h3>
              <p>{error}</p>
              <button onClick={loadPopularVideos} className="retry-button">
                Tekrar Dene
              </button>
            </div>
          )}

          {!loading && !error && videos.length > 0 && (
            <VideoResults 
              videos={videos} 
              query={searchQuery} 
              isSearchResult={hasSearched}
            />
          )}

          {!loading && !error && videos.length === 0 && hasSearched && (
            <div className="no-results">
              <h3>Sonuç bulunamadı</h3>
              <p>"{searchQuery}" için herhangi bir video bulunamadı.</p>
              <button onClick={loadPopularVideos} className="retry-button">
                Popüler Videoları Görüntüle
              </button>
            </div>
          )}

          {!loading && !error && videos.length === 0 && !hasSearched && (
            <div className="welcome-message">
              <h3>YouTube'da Arama Yapın</h3>
              <p>Yukarıdaki arama çubuğuna istediğiniz konuyu yazın ve YouTube videolarını keşfedin.</p>
            </div>
          )}
        </div>
      </main>
    </div>
  );
}

export default App;
