import React, { useState } from 'react';
import './StudySchedule.css';

const StudySchedule = ({ studySchedule, onScheduleUpdate, onSubjectSearch }) => {
  const days = ['Pazartesi', 'SalÄ±', 'Ã‡arÅŸamba', 'PerÅŸembe', 'Cuma', 'Cumartesi', 'Pazar'];
  const timeSlots = [
    { id: 'morning', label: '09:00-11:00', icon: 'ðŸŒ…' },
    { id: 'afternoon', label: '14:00-16:00', icon: 'â˜€ï¸' },
    { id: 'evening', label: '19:00-21:00', icon: 'ðŸŒ™' }
  ];

  const subjects = [
    { id: 'matematik', name: 'Matematik', color: '#FF6B6B', icon: 'ðŸ“' },
    { id: 'fizik', name: 'Fizik', color: '#4ECDC4', icon: 'âš›ï¸' },
    { id: 'kimya', name: 'Kimya', color: '#45B7D1', icon: 'ðŸ§ª' },
    { id: 'biyoloji', name: 'Biyoloji', color: '#96CEB4', icon: 'ðŸ§¬' },
    { id: 'turkce', name: 'TÃ¼rkÃ§e', color: '#FFEAA7', icon: 'ðŸ“' },
    { id: 'tarih', name: 'Tarih', color: '#DDA0DD', icon: 'ðŸ“š' },
    { id: 'cografya', name: 'CoÄŸrafya', color: '#98D8C8', icon: 'ðŸŒ' },
    { id: 'felsefe', name: 'Felsefe', color: '#F7DC6F', icon: 'ðŸ¤”' }
  ];

  const [editingCell, setEditingCell] = useState(null);

  const handleCellClick = (day, timeSlot) => {
    setEditingCell({ day, timeSlot });
  };

  const handleSubjectSelect = (subject, topic) => {
    if (editingCell) {
      onScheduleUpdate(editingCell.day, editingCell.timeSlot, subject, topic);
      setEditingCell(null);
    }
  };

  const handleVideoSearch = (day, timeSlot) => {
    const schedule = studySchedule[day]?.[timeSlot];
    if (schedule && schedule.subject && schedule.topic) {
      onSubjectSearch(schedule.subject, schedule.topic);
    }
  };

  const handleVideoClick = (schedule) => {
    if (schedule && schedule.videoUrl) {
      // Video URL'sini yeni sekmede aÃ§
      window.open(schedule.videoUrl, '_blank');
    }
  };

  const getSubjectInfo = (subjectId) => {
    return subjects.find(s => s.id === subjectId);
  };

  return (
    <div className="study-schedule">
      <div className="schedule-header">
        <h2>ðŸ“… HaftalÄ±k Ders ProgramÄ±</h2>
        <p>Her gÃ¼n iÃ§in derslerinizi planlayÄ±n ve konularÄ± belirleyin</p>
      </div>

      <div className="schedule-table-container">
        <table className="schedule-table">
          <thead>
            <tr>
              <th className="time-header">Zaman</th>
              {days.map(day => (
                <th key={day} className="day-header">
                  {day}
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {timeSlots.map(timeSlot => (
              <tr key={timeSlot.id}>
                <td className="time-cell">
                  <span className="time-icon">{timeSlot.icon}</span>
                  <span className="time-label">{timeSlot.label}</span>
                </td>
                {days.map(day => {
                  const schedule = studySchedule[day]?.[timeSlot.id];
                  const subjectInfo = schedule ? getSubjectInfo(schedule.subject) : null;
                  
                  return (
                    <td 
                      key={`${day}-${timeSlot.id}`}
                      className="schedule-cell"
                      onClick={() => handleCellClick(day, timeSlot.id)}
                    >
                      {schedule ? (
                        <div className="scheduled-lesson">
                          <div 
                            className="subject-badge"
                            style={{ backgroundColor: subjectInfo?.color || '#ccc' }}
                          >
                            <span className="subject-icon">{subjectInfo?.icon}</span>
                            <span className="subject-name">{subjectInfo?.name}</span>
                          </div>
                          <div className="topic-name">{schedule.topic}</div>
                          <button 
                            className="video-search-btn"
                            onClick={(e) => {
                              e.stopPropagation();
                              handleVideoSearch(day, timeSlot.id);
                            }}
                            title="Bu konu iÃ§in video ara"
                          >
                            ðŸŽ¥ Video Ara
                          </button>
                        </div>
                      ) : (
                        <div className="empty-cell">
                          <span className="add-icon">+</span>
                          <span className="add-text">Ders Ekle</span>
                        </div>
                      )}
                    </td>
                  );
                })}
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {editingCell && (
        <SubjectTopicModal
          subjects={subjects}
          onSelect={handleSubjectSelect}
          onClose={() => setEditingCell(null)}
        />
      )}
    </div>
  );
};

const SubjectTopicModal = ({ subjects, onSelect, onClose }) => {
  const [selectedSubject, setSelectedSubject] = useState('');
  const [topic, setTopic] = useState('');

  const commonTopics = {
    matematik: ['Fonksiyonlar', 'TÃ¼rev', 'Ä°ntegral', 'Limit', 'Trigonometri', 'Logaritma'],
    fizik: ['Mekanik', 'Elektrik', 'Manyetizma', 'Optik', 'Termodinamik', 'Dalgalar'],
    kimya: ['Atom Teorisi', 'Kimyasal BaÄŸlar', 'Reaksiyonlar', 'Ã‡Ã¶zeltiler', 'Organik Kimya', 'Elektrokimya'],
    biyoloji: ['HÃ¼cre', 'Genetik', 'Evrim', 'Ekosistem', 'Ä°nsan Fizyolojisi', 'Bitki Biyolojisi'],
    turkce: ['Dil Bilgisi', 'Edebiyat', 'Kompozisyon', 'Anlam Bilgisi', 'YazÄ±m KurallarÄ±', 'Noktalama'],
    tarih: ['OsmanlÄ± Tarihi', 'Cumhuriyet Tarihi', 'DÃ¼nya Tarihi', 'Ä°nkÄ±lap Tarihi', 'TÃ¼rk Tarihi', 'Medeniyetler'],
    cografya: ['Fiziki CoÄŸrafya', 'BeÅŸeri CoÄŸrafya', 'TÃ¼rkiye CoÄŸrafyasÄ±', 'DÃ¼nya CoÄŸrafyasÄ±', 'Ä°klim', 'NÃ¼fus'],
    felsefe: ['Felsefe Tarihi', 'MantÄ±k', 'Etik', 'Estetik', 'Bilgi Felsefesi', 'VarlÄ±k Felsefesi']
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (selectedSubject && topic) {
      onSelect(selectedSubject, topic);
    }
  };

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <div className="modal-header">
          <h3>Ders ve Konu SeÃ§in</h3>
