import React, { useState, useEffect } from 'react';
import { analyticsService } from '../services/analyticsService';
import './Quiz.css';

const Quiz = ({ subject, onClose }) => {
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [showResult, setShowResult] = useState(false);
  const [isCorrect, setIsCorrect] = useState(false);
  const [score, setScore] = useState({ correct: 0, wrong: 0, total: 0 });
  const [questions, setQuestions] = useState([]);
  const [loading, setLoading] = useState(true);

  // Örnek sorular - gerçek uygulamada API'den gelecek
  const sampleQuestions = {
    matematik: [
      {
        id: 1,
        question: "2x + 5 = 13 denkleminin çözümü nedir?",
        options: ["x = 4", "x = 3", "x = 5", "x = 6"],
        correct: 0,
        explanation: "2x + 5 = 13 → 2x = 8 → x = 4"
      },
      {
        id: 2,
        question: "Bir üçgenin iç açıları toplamı kaç derecedir?",
        options: ["180°", "90°", "360°", "270°"],
        correct: 0,
        explanation: "Üçgenin iç açıları toplamı her zaman 180 derecedir."
      },
      {
        id: 3,
        question: "√16 değeri nedir?",
        options: ["4", "8", "2", "16"],
        correct: 0,
        explanation: "√16 = 4 çünkü 4² = 16"
      }
    ],
    fizik: [
      {
        id: 1,
        question: "Newton'un ikinci yasası nedir?",
        options: ["F = ma", "E = mc²", "F = G(m₁m₂)/r²", "v = v₀ + at"],
        correct: 0,
        explanation: "Newton'un ikinci yasası: F = ma (Kuvvet = kütle × ivme)"
      },
      {
        id: 2,
        question: "Işık hızı yaklaşık olarak kaç m/s'dir?",
        options: ["3 × 10⁸", "3 × 10⁶", "3 × 10¹⁰", "3 × 10⁴"],
        correct: 0,
        explanation: "Işık hızı boşlukta yaklaşık 3 × 10⁸ m/s'dir."
      }
    ],
    kimya: [
      {
        id: 1,
        question: "Su molekülünün formülü nedir?",
        options: ["H₂O", "CO₂", "H₂SO₄", "NaCl"],
        correct: 0,
        explanation: "Su molekülü H₂O formülüne sahiptir."
      },
      {
        id: 2,
        question: "Periyodik tabloda kaç element vardır?",
        options: ["118", "92", "103", "126"],
        correct: 0,
        explanation: "Periyodik tabloda 118 element bulunmaktadır."
      }
    ],
    biyoloji: [
      {
        id: 1,
        question: "Hücrenin enerji üretim merkezi hangisidir?",
        options: ["Mitokondri", "Ribozom", "Golgi", "Endoplazmik retikulum"],
        correct: 0,
        explanation: "Mitokondri hücrenin enerji üretim merkezidir."
      },
      {
        id: 2,
        question: "DNA'nın yapı taşları nelerdir?",
        options: ["Nükleotidler", "Amino asitler", "Monosakkaritler", "Yağ asitleri"],
        correct: 0,
        explanation: "DNA'nın yapı taşları nükleotidlerdir."
      }
    ]
  };

  useEffect(() => {
    // Soruları yükle
    const subjectQuestions = sampleQuestions[subject] || sampleQuestions.matematik;
    setQuestions(subjectQuestions);
    setLoading(false);
  }, [subject]);

  const handleAnswerSelect = (answerIndex) => {
    if (showResult) return;
    setSelectedAnswer(answerIndex);
  };

  const handleSubmitAnswer = () => {
    if (selectedAnswer === null) return;
    
    const question = questions[currentQuestion];
    const correct = selectedAnswer === question.correct;
    
    setIsCorrect(correct);
    setShowResult(true);
    
    // Skoru güncelle
    setScore(prev => ({
      correct: prev.correct + (correct ? 1 : 0),
      wrong: prev.wrong + (correct ? 0 : 1),
      total: prev.total + 1
    }));

    // Analiz servisine kaydet
    const dayOfWeek = getCurrentDayOfWeek();
    analyticsService.recordQuestionResult(subject, correct, dayOfWeek);
  };

  const handleNextQuestion = () => {
    if (currentQuestion < questions.length - 1) {
      setCurrentQuestion(prev => prev + 1);
      setSelectedAnswer(null);
      setShowResult(false);
    } else {
      // Quiz tamamlandı
      handleQuizComplete();
    }
  };

  const handleQuizComplete = () => {
    onClose();
  };

  const getCurrentDayOfWeek = () => {
    const days = ['sunday', 'monday', 'tuesday', 'wednesday', 'thursday', 'friday', 'saturday'];
    return days[new Date().getDay()];
  };

  const getSubjectName = (subject) => {
    const names = {
      matematik: 'Matematik',
      fizik: 'Fizik',
      kimya: 'Kimya',
      biyoloji: 'Biyoloji',
      turkce: 'Türkçe',
      tarih: 'Tarih',
      cografya: 'Coğrafya',
      felsefe: 'Felsefe'
    };
    return names[subject] || subject;
  };

  if (loading) {
    return (
      <div className="quiz-overlay">
        <div className="quiz-container">
          <div className="loading-spinner"></div>
          <p>Sorular yükleniyor...</p>
        </div>
      </div>
    );
  }

  if (questions.length === 0) {
    return (
      <div className="quiz-overlay">
        <div className="quiz-container">
          <h2>Quiz</h2>
          <p>Bu ders için henüz soru bulunmuyor.</p>
          <button onClick={onClose} className="quiz-btn quiz-btn-primary">
            Kapat
          </button>
        </div>
      </div>
    );
  }

  const question = questions[currentQuestion];
  const progress = ((currentQuestion + 1) / questions.length) * 100;

  return (
    <div className="quiz-overlay">
      <div className="quiz-container">
        <div className="quiz-header">
          <h2>{getSubjectName(subject)} Quiz</h2>
          <button onClick={onClose} className="quiz-close-btn">×</button>
        </div>

        <div className="quiz-progress">
          <div className="progress-bar">
            <div 
              className="progress-fill" 
              style={{ width: `${progress}%` }}
            ></div>
          </div>
          <span className="progress-text">
            {currentQuestion + 1} / {questions.length}
          </span>
        </div>

        <div className="quiz-score">
          <span>Doğru: {score.correct}</span>
          <span>Yanlış: {score.wrong}</span>
          <span>Toplam: {score.total}</span>
        </div>

        <div className="quiz-question">
          <h3>{question.question}</h3>
        </div>

        <div className="quiz-options">
          {question.options.map((option, index) => (
            <button
              key={index}
              className={`quiz-option ${
                selectedAnswer === index ? 'selected' : ''
              } ${
                showResult 
                  ? index === question.correct 
                    ? 'correct' 
                    : selectedAnswer === index && !isCorrect 
                      ? 'wrong' 
                      : ''
                  : ''
              }`}
              onClick={() => handleAnswerSelect(index)}
              disabled={showResult}
            >
              {option}
            </button>
          ))}
        </div>

        {showResult && (
          <div className="quiz-result">
            <div className={`result-message ${isCorrect ? 'correct' : 'wrong'}`}>
              {isCorrect ? '✅ Doğru!' : '❌ Yanlış!'}
            </div>
            <div className="explanation">
              <strong>Açıklama:</strong> {question.explanation}
            </div>
          </div>
        )}

        <div className="quiz-actions">
          {!showResult ? (
            <button
              onClick={handleSubmitAnswer}
              disabled={selectedAnswer === null}
              className="quiz-btn quiz-btn-primary"
            >
              Cevabı Gönder
            </button>
          ) : (
            <button
              onClick={handleNextQuestion}
              className="quiz-btn quiz-btn-primary"
            >
              {currentQuestion < questions.length - 1 ? 'Sonraki Soru' : 'Quiz Tamamla'}
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default Quiz;
