import React, { useState, useEffect } from 'react';
import StudySchedule from './components/StudySchedule';
import SubjectSelector from './components/SubjectSelector';
import VideoResults from './components/VideoResults';
import LoadingSkeleton from './components/LoadingSkeleton';
import ThemeToggle from './components/ThemeToggle';
import { searchVideos, getPopularVideos } from './services/youtubeApi';
import './App.css';

function App() {
  const [videos, setVideos] = useState([]);
  const [loading, setLoading] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [error, setError] = useState('');
  const [hasSearched, setHasSearched] = useState(false);
  const [selectedSubject, setSelectedSubject] = useState('');
  const [studySchedule, setStudySchedule] = useState({});
  const [currentView, setCurrentView] = useState('schedule'); // 'schedule' or 'videos'

  // Sayfa yüklendiğinde popüler videoları getir
  useEffect(() => {
    loadPopularVideos();
  }, []);

  const loadPopularVideos = async () => {
    try {
      setLoading(true);
      setError('');
      const popularVideos = await getPopularVideos();
      setVideos(popularVideos);
      setHasSearched(false);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = async (query) => {
    if (!query.trim()) return;
    
    setLoading(true);
    setSearchQuery(query);
    setError('');
    setHasSearched(true);
    setSelectedCategory('all');
    
    try {
      const results = await searchVideos(query);
      setVideos(results);
    } catch (err) {
      setError(err.message);
      setVideos([]);
    } finally {
      setLoading(false);
    }
  };

  const handleCategoryChange = async (categoryId) => {
    setSelectedCategory(categoryId);
    
    if (categoryId === 'all') {
      loadPopularVideos();
      return;
    }
    
    setLoading(true);
    setError('');
    setHasSearched(true);
    setSearchQuery('');
    
    try {
      // Kategoriye göre arama yap
      const categoryQueries = {
        music: 'müzik şarkı',
        gaming: 'oyun gameplay',
        education: 'eğitim öğrenme',
        tech: 'teknoloji programlama',
        entertainment: 'eğlence komedi',
        sports: 'spor futbol',
        news: 'haber güncel'
      };
      
      const query = categoryQueries[categoryId] || categoryId;
      const results = await searchVideos(query);
      setVideos(results);
    } catch (err) {
      setError(err.message);
      setVideos([]);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <ThemeToggle />
      <header className="app-header">
        <div className="container">
          <h1 className="app-title">
            <span className="youtube-red">You</span>Tube Arama Motoru
          </h1>
          <p className="app-subtitle">YouTube videolarını keşfedin ve arayın</p>
        </div>
      </header>

      <main className="app-main">
        <div className="container">
          <SearchBar onSearch={handleSearch} loading={loading} />
          <CategoryFilter 
            selectedCategory={selectedCategory}
            onCategoryChange={handleCategoryChange}
            loading={loading}
          />
          
          {loading && (
            <div className="loading-container">
              <div className="loading-spinner"></div>
              <p>{hasSearched ? 'Aranıyor...' : 'Popüler videolar yükleniyor...'}</p>
              <LoadingSkeleton count={6} />
            </div>
          )}

          {error && (
            <div className="error-container">
              <h3>Hata Oluştu</h3>
              <p>{error}</p>
              <button onClick={loadPopularVideos} className="retry-button">
                Tekrar Dene
              </button>
            </div>
          )}

          {!loading && !error && videos.length > 0 && (
            <VideoResults 
              videos={videos} 
              query={searchQuery} 
              isSearchResult={hasSearched}
            />
          )}

          {!loading && !error && videos.length === 0 && hasSearched && (
            <div className="no-results">
              <h3>Sonuç bulunamadı</h3>
              <p>"{searchQuery}" için herhangi bir video bulunamadı.</p>
              <button onClick={loadPopularVideos} className="retry-button">
                Popüler Videoları Görüntüle
              </button>
            </div>
          )}

          {!loading && !error && videos.length === 0 && !hasSearched && (
            <div className="welcome-message">
              <h3>YouTube'da Arama Yapın</h3>
              <p>Yukarıdaki arama çubuğuna istediğiniz konuyu yazın ve YouTube videolarını keşfedin.</p>
            </div>
          )}
        </div>
      </main>
    </div>
  );
}

export default App;
