import React from 'react';
import VideoCard from './VideoCard';
import './VideoResults.css';

const getTimeSlotLabel = (timeSlot) => {
  const timeSlots = {
    'morning': '09:00-11:00',
    'afternoon': '14:00-16:00',
    'evening': '19:00-21:00'
  };
  return timeSlots[timeSlot] || timeSlot;
};

const VideoResults = ({ videos, query, isSearchResult, onAddToSchedule, showAddButton = false, selectedDay = null, selectedTimeSlot = null }) => {
  return (
    <div className="video-results">
      <div className="results-header">
        <h2>
          {isSearchResult ? (
            <>"{query}" i√ßin {videos.length} sonu√ß bulundu</>
          ) : (
            <>Pop√ºler Videolar ({videos.length})</>
          )}
        </h2>
        
        {/* Sƒ±ralama Bilgisi */}
        {isSearchResult && videos.length > 0 && (
          <div className="sorting-info">
            <div className="sorting-badge">
              <span className="sorting-icon">‚≠ê</span>
              <span className="sorting-text">Kalite Skoruna G√∂re Sƒ±ralandƒ±</span>
            </div>
            <div className="sorting-details">
              <span className="sorting-detail">üìä Beƒüeni oranƒ±</span>
              <span className="sorting-detail">üëÄ ƒ∞zlenme sayƒ±sƒ±</span>
              <span className="sorting-detail">üí¨ Etkile≈üim oranƒ±</span>
              <span className="sorting-detail">üìÖ G√ºncellik</span>
            </div>
          </div>
        )}
        
        {showAddButton && (
          <p className="add-instruction">
            {selectedDay && selectedTimeSlot ? (
              <>
                üìÖ Videolar <strong>{selectedDay} {getTimeSlotLabel(selectedTimeSlot)}</strong> saatine eklenecek
                <br />
                üí° Beƒüendiƒüiniz videoyu <strong>"Programa Ekle"</strong> butonuna tƒ±klayarak direkt ekleyin
              </>
            ) : (
              <>
                üí° Beƒüendiƒüiniz videolarƒ± ders programƒ±nƒ±za eklemek i√ßin <strong>"Programa Ekle"</strong> butonuna tƒ±klayƒ±n
              </>
            )}
          </p>
        )}
      </div>
      
      <div className="video-grid">
        {videos.map((video) => (
          <VideoCard 
            key={video.id} 
            video={video} 
            onAddToSchedule={onAddToSchedule}
            showAddButton={showAddButton}
            selectedDay={selectedDay}
            selectedTimeSlot={selectedTimeSlot}
          />
        ))}
      </div>
    </div>
  );
};

export default VideoResults;
