import React, { useState } from 'react';
import './StudySchedule.css';

const StudySchedule = ({ studySchedule, onScheduleUpdate, onSubjectSearch }) => {
  const days = ['Pazartesi', 'Salı', 'Çarşamba', 'Perşembe', 'Cuma', 'Cumartesi', 'Pazar'];
  const timeSlots = [
    { id: 'morning', label: '09:00-11:00', icon: '🌅' },
    { id: 'afternoon', label: '14:00-16:00', icon: '☀️' },
    { id: 'evening', label: '19:00-21:00', icon: '🌙' }
  ];

  const subjects = [
    { id: 'matematik', name: 'Matematik', color: '#FF6B6B', icon: '📐' },
    { id: 'fizik', name: 'Fizik', color: '#4ECDC4', icon: '⚛️' },
    { id: 'kimya', name: 'Kimya', color: '#45B7D1', icon: '🧪' },
    { id: 'biyoloji', name: 'Biyoloji', color: '#96CEB4', icon: '🧬' },
    { id: 'turkce', name: 'Türkçe', color: '#FFEAA7', icon: '📝' },
    { id: 'tarih', name: 'Tarih', color: '#DDA0DD', icon: '📚' },
    { id: 'cografya', name: 'Coğrafya', color: '#98D8C8', icon: '🌍' },
    { id: 'felsefe', name: 'Felsefe', color: '#F7DC6F', icon: '🤔' }
  ];

  const [editingCell, setEditingCell] = useState(null);

  const handleCellClick = (day, timeSlot) => {
    setEditingCell({ day, timeSlot });
  };

  const handleSubjectSelect = (subject, topic) => {
    if (editingCell) {
      onScheduleUpdate(editingCell.day, editingCell.timeSlot, subject, topic);
      setEditingCell(null);
    }
  };

  const handleVideoSearch = (day, timeSlot) => {
    const schedule = studySchedule[day]?.[timeSlot];
    if (schedule && schedule.subject && schedule.topic) {
      onSubjectSearch(schedule.subject, schedule.topic);
    }
  };

  const getSubjectInfo = (subjectId) => {
    return subjects.find(s => s.id === subjectId);
  };

  return (
    <div className="study-schedule">
      <div className="schedule-header">
        <h2>📅 Haftalık Ders Programı</h2>
        <p>Her gün için derslerinizi planlayın ve konuları belirleyin</p>
      </div>

      <div className="schedule-table-container">
        <table className="schedule-table">
          <thead>
            <tr>
              <th className="time-header">Zaman</th>
              {days.map(day => (
                <th key={day} className="day-header">
                  {day}
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {timeSlots.map(timeSlot => (
              <tr key={timeSlot.id}>
                <td className="time-cell">
                  <span className="time-icon">{timeSlot.icon}</span>
                  <span className="time-label">{timeSlot.label}</span>
                </td>
                {days.map(day => {
                  const schedule = studySchedule[day]?.[timeSlot.id];
                  const subjectInfo = schedule ? getSubjectInfo(schedule.subject) : null;
                  
                  return (
                    <td 
                      key={`${day}-${timeSlot.id}`}
                      className="schedule-cell"
                      onClick={() => handleCellClick(day, timeSlot.id)}
                    >
                      {schedule ? (
                        <div className="scheduled-lesson">
                          <div 
                            className="subject-badge"
                            style={{ backgroundColor: subjectInfo?.color || '#ccc' }}
                          >
                            <span className="subject-icon">{subjectInfo?.icon}</span>
                            <span className="subject-name">{subjectInfo?.name}</span>
                          </div>
                          <div className="topic-name">{schedule.topic}</div>
                          <button 
                            className="video-search-btn"
                            onClick={(e) => {
                              e.stopPropagation();
                              handleVideoSearch(day, timeSlot.id);
                            }}
                            title="Bu konu için video ara"
                          >
                            🎥 Video Ara
                          </button>
                        </div>
                      ) : (
                        <div className="empty-cell">
                          <span className="add-icon">+</span>
                          <span className="add-text">Ders Ekle</span>
                        </div>
                      )}
                    </td>
                  );
                })}
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {editingCell && (
        <SubjectTopicModal
          subjects={subjects}
          onSelect={handleSubjectSelect}
          onClose={() => setEditingCell(null)}
        />
      )}
    </div>
  );
};

const SubjectTopicModal = ({ subjects, onSelect, onClose }) => {
  const [selectedSubject, setSelectedSubject] = useState('');
  const [topic, setTopic] = useState('');

  const commonTopics = {
    matematik: ['Fonksiyonlar', 'Türev', 'İntegral', 'Limit', 'Trigonometri', 'Logaritma'],
    fizik: ['Mekanik', 'Elektrik', 'Manyetizma', 'Optik', 'Termodinamik', 'Dalgalar'],
    kimya: ['Atom Teorisi', 'Kimyasal Bağlar', 'Reaksiyonlar', 'Çözeltiler', 'Organik Kimya', 'Elektrokimya'],
    biyoloji: ['Hücre', 'Genetik', 'Evrim', 'Ekosistem', 'İnsan Fizyolojisi', 'Bitki Biyolojisi'],
    turkce: ['Dil Bilgisi', 'Edebiyat', 'Kompozisyon', 'Anlam Bilgisi', 'Yazım Kuralları', 'Noktalama'],
    tarih: ['Osmanlı Tarihi', 'Cumhuriyet Tarihi', 'Dünya Tarihi', 'İnkılap Tarihi', 'Türk Tarihi', 'Medeniyetler'],
    cografya: ['Fiziki Coğrafya', 'Beşeri Coğrafya', 'Türkiye Coğrafyası', 'Dünya Coğrafyası', 'İklim', 'Nüfus'],
    felsefe: ['Felsefe Tarihi', 'Mantık', 'Etik', 'Estetik', 'Bilgi Felsefesi', 'Varlık Felsefesi']
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (selectedSubject && topic) {
      onSelect(selectedSubject, topic);
    }
  };

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <div className="modal-header">
          <h3>Ders ve Konu Seçin</h3>
          <button className="close-btn" onClick={onClose}>×</button>
        </div>
        
        <form onSubmit={handleSubmit} className="modal-form">
          <div className="form-group">
            <label>Ders Seçin:</label>
            <div className="subject-grid">
              {subjects.map(subject => (
                <button
                  key={subject.id}
                  type="button"
                  className={`subject-option ${selectedSubject === subject.id ? 'selected' : ''}`}
                  onClick={() => setSelectedSubject(subject.id)}
                  style={{ borderColor: subject.color }}
                >
                  <span className="subject-icon">{subject.icon}</span>
                  <span className="subject-name">{subject.name}</span>
                </button>
              ))}
            </div>
          </div>

          {selectedSubject && (
            <div className="form-group">
              <label>Konu Seçin:</label>
              <select 
                value={topic} 
                onChange={(e) => setTopic(e.target.value)}
                className="topic-select"
                required
              >
                <option value="">Konu seçin...</option>
                {commonTopics[selectedSubject]?.map(topicName => (
                  <option key={topicName} value={topicName}>{topicName}</option>
                ))}
              </select>
            </div>
          )}

          <div className="form-actions">
            <button type="button" onClick={onClose} className="cancel-btn">
              İptal
            </button>
            <button type="submit" className="submit-btn" disabled={!selectedSubject || !topic}>
              Kaydet
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default StudySchedule;
