import React, { useState } from 'react';
import './PackageDetailModal.css';

const PackageDetailModal = ({ packageData, onClose }) => {
  const [selectedWeek, setSelectedWeek] = useState(1);

  // Haftalık program verileri
  const weeklyPrograms = {
    1: {
      title: 'YKS ÖĞRENCİLERİ İÇİN DERS ÇALIŞMA PROGRAMI (SAYISAL) 1. HAFTA',
      schedule: {
        monday: {
          '13:00-14:50': { subject: 'Türkçe', topic: 'Sözcükte Anlam', type: 'lesson' },
          '15:00-16:50': { subject: 'Matematik', topic: 'Temel Kavramlar', type: 'lesson' },
          '17:00-18:50': { subject: 'Fizik', topic: 'Fizik Bilimine Giriş', type: 'lesson' },
          '19:00-20:50': { subject: 'Coğrafya', topic: 'Doğal Sistemler; İnsan, Doğa ve Coğrafya', type: 'lesson' }
        },
        tuesday: {
          '13:00-13:50': { subject: 'Türkçe', topic: '30 Paragraf Soru Çözümü', type: 'practice' },
          '14:00-15:50': { subject: 'Matematik', topic: 'Temel Kavramlar', type: 'lesson' },
          '16:00-17:50': { subject: 'Fizik', topic: 'Fizik Bilimine Giriş', type: 'lesson' },
          '18:00-19:50': { subject: 'Kimya', topic: 'Kimya Bilimi', type: 'lesson' },
          '20:00-20:50': { subject: 'Felsefe', topic: 'Felsefenin Alanı', type: 'lesson' }
        },
        wednesday: {
          '13:00-13:50': { subject: 'Türkçe', topic: '30 Paragraf Soru Çözümü', type: 'practice' },
          '14:00-15:50': { subject: 'Geometri', topic: 'Üçgenler', type: 'lesson' },
          '16:00-17:50': { subject: 'Biyoloji', topic: 'Biyoloji Bilimi', type: 'lesson' },
          '18:00-19:50': { subject: 'Matematik', topic: 'Temel Kavramlar', type: 'lesson' },
          '20:00-20:50': { subject: 'Din Kültürü', topic: 'Kuran-ı Kerim\'in Anlaşılması ve Yorumu', type: 'lesson' }
        },
        thursday: {
          '13:00-14:50': { subject: 'Biyoloji', topic: 'Biyoloji Bilimi', type: 'lesson' },
          '15:00-16:50': { subject: 'Geometri', topic: 'Üçgenler', type: 'lesson' },
          '17:00-18:50': { subject: 'Kimya', topic: 'Kimya Bilimi', type: 'lesson' },
          '19:00-20:50': { subject: 'Tarih', topic: 'Tarih Bilimine Giriş', type: 'lesson' }
        },
        friday: {
          '13:00-14:50': { subject: 'Biyoloji', topic: 'Biyoloji Bilimi', type: 'lesson' },
          '15:00-16:50': { subject: 'Matematik', topic: 'Temel Kavramlar', type: 'lesson' },
          '17:00-18:50': { subject: 'Kimya', topic: 'Kimya Bilimi', type: 'lesson' },
          '19:00-20:50': { subject: 'Fizik', topic: 'Fizik Bilimine Giriş', type: 'lesson' }
        },
        saturday: {
          '13:00-16:50': { subject: 'Genel', topic: 'HAFTALIK GENEL TEKRAR', type: 'review' },
          '17:00-20:50': { subject: 'Genel', topic: 'HER DERSTEN EN AZ 20 SORU ÇÖZÜMÜ', type: 'practice' }
        },
        sunday: {
          '13:00-14:50': { subject: 'Genel', topic: 'TYT DENEME SINAVI', type: 'exam' },
          '19:00-20:50': { subject: 'Genel', topic: 'AYT DENEME SINAVI', type: 'exam' }
        }
      },
      notes: [
        'GÜNLÜK BİR SAAT GENEL TEKRAR',
        'GÜNLÜK EN AZ YARIM SAAT KİTAP OKUMA SAATİ'
      ]
    },
    2: {
      title: 'YKS ÖĞRENCİLERİ İÇİN DERS ÇALIŞMA PROGRAMI (SAYISAL) 2. HAFTA',
      schedule: {
        monday: {
          '13:00-14:50': { subject: 'Türkçe', topic: 'Cümlede Anlam', type: 'lesson' },
          '15:00-16:50': { subject: 'Matematik', topic: 'Sayılar', type: 'lesson' },
          '17:00-18:50': { subject: 'Fizik', topic: 'Madde ve Özellikleri', type: 'lesson' },
          '19:00-20:50': { subject: 'Coğrafya', topic: 'Beşeri Sistemler', type: 'lesson' }
        },
        tuesday: {
          '13:00-13:50': { subject: 'Türkçe', topic: '30 Paragraf Soru Çözümü', type: 'practice' },
          '14:00-15:50': { subject: 'Matematik', topic: 'Sayılar', type: 'lesson' },
          '16:00-17:50': { subject: 'Fizik', topic: 'Madde ve Özellikleri', type: 'lesson' },
          '18:00-19:50': { subject: 'Kimya', topic: 'Atom ve Periyodik Sistem', type: 'lesson' },
          '20:00-20:50': { subject: 'Felsefe', topic: 'Bilgi Felsefesi', type: 'lesson' }
        },
        wednesday: {
          '13:00-13:50': { subject: 'Türkçe', topic: '30 Paragraf Soru Çözümü', type: 'practice' },
          '14:00-15:50': { subject: 'Geometri', topic: 'Üçgenler', type: 'lesson' },
          '16:00-17:50': { subject: 'Biyoloji', topic: 'Hücre', type: 'lesson' },
          '18:00-19:50': { subject: 'Matematik', topic: 'Sayılar', type: 'lesson' },
          '20:00-20:50': { subject: 'Din Kültürü', topic: 'İslam ve İbadet', type: 'lesson' }
        },
        thursday: {
          '13:00-14:50': { subject: 'Biyoloji', topic: 'Hücre', type: 'lesson' },
          '15:00-16:50': { subject: 'Geometri', topic: 'Üçgenler', type: 'lesson' },
          '17:00-18:50': { subject: 'Kimya', topic: 'Atom ve Periyodik Sistem', type: 'lesson' },
          '19:00-20:50': { subject: 'Tarih', topic: 'İlk Çağ Tarihi', type: 'lesson' }
        },
        friday: {
          '13:00-14:50': { subject: 'Biyoloji', topic: 'Hücre', type: 'lesson' },
          '15:00-16:50': { subject: 'Matematik', topic: 'Sayılar', type: 'lesson' },
          '17:00-18:50': { subject: 'Kimya', topic: 'Atom ve Periyodik Sistem', type: 'lesson' },
          '19:00-20:50': { subject: 'Fizik', topic: 'Madde ve Özellikleri', type: 'lesson' }
        },
        saturday: {
          '13:00-16:50': { subject: 'Genel', topic: 'HAFTALIK GENEL TEKRAR', type: 'review' },
          '17:00-20:50': { subject: 'Genel', topic: 'HER DERSTEN EN AZ 20 SORU ÇÖZÜMÜ', type: 'practice' }
        },
        sunday: {
          '13:00-14:50': { subject: 'Genel', topic: 'TYT DENEME SINAVI', type: 'exam' },
          '19:00-20:50': { subject: 'Genel', topic: 'AYT DENEME SINAVI', type: 'exam' }
        }
      },
      notes: [
        'GÜNLÜK BİR SAAT GENEL TEKRAR',
        'GÜNLÜK EN AZ YARIM SAAT KİTAP OKUMA SAATİ'
      ]
    }
  };

  const daysOfWeek = [
    { id: 'monday', label: 'Pazartesi', short: 'Pzt' },
    { id: 'tuesday', label: 'Salı', short: 'Sal' },
    { id: 'wednesday', label: 'Çarşamba', short: 'Çar' },
    { id: 'thursday', label: 'Perşembe', short: 'Per' },
    { id: 'friday', label: 'Cuma', short: 'Cum' },
    { id: 'saturday', label: 'Cumartesi', short: 'Cmt' },
    { id: 'sunday', label: 'Pazar', short: 'Paz' }
  ];

  const getSubjectIcon = (subject) => {
    const icons = {
      'Türkçe': '📝',
      'Matematik': '📐',
      'Fizik': '⚡',
      'Kimya': '🧪',
      'Biyoloji': '🧬',
      'Geometri': '📏',
      'Coğrafya': '🌍',
      'Tarih': '📚',
      'Felsefe': '🤔',
      'Din Kültürü': '🕌',
      'Genel': '📋'
    };
    return icons[subject] || '📖';
  };

  const getTypeColor = (type) => {
    const colors = {
      'lesson': '#667eea',
      'practice': '#10b981',
      'review': '#f59e0b',
      'exam': '#ef4444'
    };
    return colors[type] || '#6b7280';
  };

  const getTypeLabel = (type) => {
    const labels = {
      'lesson': 'Ders',
      'practice': 'Soru Çözümü',
      'review': 'Tekrar',
      'exam': 'Deneme'
    };
    return labels[type] || 'Genel';
  };

  const currentProgram = weeklyPrograms[selectedWeek];

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="package-detail-modal" onClick={(e) => e.stopPropagation()}>
        <div className="modal-header">
          <h2>📚 {packageData.title}</h2>
          <button className="close-btn" onClick={onClose}>✕</button>
        </div>

        <div className="modal-content">
          {/* Package Info */}
          <div className="package-info">
            <div className="package-badge">
              <span>{packageData.successRate}</span>
            </div>
            <div className="package-price">
              <span className="price">{packageData.price}</span>
              <span className="installment">Peşin Fiyatına 12 Taksit ({packageData.installment})</span>
            </div>
          </div>

          {/* Week Selector */}
          <div className="week-selector-section">
            <h3>📅 Haftalık Program</h3>
          </div>

          {/* Weekly Program */}
          <div className="weekly-program">
            <h4>YKS ÖĞRENCİLERİ İÇİN DERS ÇALIŞMA PROGRAMI (SAYISAL)</h4>
            
            <div className="weeks-container">
              {[1, 2].map(week => {
                const weekProgram = weeklyPrograms[week];
                return (
                  <div key={week} className="week-program">
                    <h5>{week}. HAFTA</h5>
                    
                    <div className="program-table">
                      <div className="table-header">
                        <div className="time-column">ÇALIŞMA SAATLERİ</div>
                        <div className="days-row">
                          {daysOfWeek.map(day => (
                            <div key={day.id} className="day-header">
                              <span className="day-full">{day.label}</span>
                              <span className="day-short">{day.short}</span>
                            </div>
                          ))}
                        </div>
                      </div>

                      <div className="table-body">
                        {Object.entries(weekProgram.schedule).map(([day, timeSlots]) => (
                          <div key={day} className="day-row">
                            <div className="day-label">{daysOfWeek.find(d => d.id === day)?.label}</div>
                            <div className="day-content">
                              {daysOfWeek.map(dayOfWeek => {
                                const dayTimeSlots = weekProgram.schedule[dayOfWeek.id] || {};
                                return (
                                  <div key={dayOfWeek.id} className="day-column">
                                    {Object.entries(dayTimeSlots).map(([time, lesson]) => (
                                      <div key={time} className="lesson-slot">
                                        <div className="lesson-time">{time}</div>
                                        <div 
                                          className="lesson-content"
                                          style={{ borderLeftColor: getTypeColor(lesson.type) }}
                                        >
                                          <div className="lesson-header">
                                            <span className="subject-icon">{getSubjectIcon(lesson.subject)}</span>
                                            <span className="subject-name">{lesson.subject}</span>
                                            <span 
                                              className="lesson-type"
                                              style={{ backgroundColor: getTypeColor(lesson.type) }}
                                            >
                                              {getTypeLabel(lesson.type)}
                                            </span>
                                          </div>
                                          <div className="lesson-topic">{lesson.topic}</div>
                                        </div>
                                      </div>
                                    ))}
                                  </div>
                                );
                              })}
                            </div>
                          </div>
                        ))}
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>

            {/* Notes */}
            <div className="program-notes">
              <h5>📝 Önemli Notlar</h5>
              <ul>
                {currentProgram.notes.map((note, index) => (
                  <li key={index}>{note}</li>
                ))}
              </ul>
            </div>
          </div>

          {/* Features */}
          <div className="package-features">
            <h3>✨ Paket Özellikleri</h3>
            <div className="features-grid">
              {packageData.features.map((feature, index) => (
                <div key={index} className="feature-item">
                  <span className="check-icon">✓</span>
                  <span>{feature}</span>
                </div>
              ))}
            </div>
          </div>

          {/* Curriculum */}
          <div className="package-curriculum">
            <h3>📚 Müfredat</h3>
            <div className="curriculum-items">
              <div className="curriculum-item">
                <span className="check-icon">✓</span>
                {packageData.curriculum.videos}
              </div>
              <div className="curriculum-item">
                <span className="check-icon">✓</span>
                {packageData.curriculum.questions}
              </div>
            </div>
          </div>
        </div>

        <div className="modal-footer">
          <button className="close-modal-btn" onClick={onClose}>
            Kapat
          </button>
        </div>
      </div>
    </div>
  );
};

export default PackageDetailModal;
